import React, { useContext, useState } from 'react';
import {Link,useNavigate} from "react-router-dom"
import Navbar from './Navbar';
import productcontext from '../context/Productcontext';

export default function Login() {
   const {valid,setvalid}= useContext(productcontext)
    
  const [input,setinput] = useState({email:"",password:""})
  
  let navi = useNavigate();

  const onchange =(e)=>{
    e.preventDefault();
    setinput({
    ...input,[e.target.name]:e.target.value
  
    }) }
    
    const Login = async(e)=>{
        e.preventDefault();
        const {email,password}=input;
        
       const loginuser = await fetch("http://localhost:5000/api/auth",{
    
      headers:{"content-type":"application/json"},
      method:"POST",
      body:JSON.stringify({
       email:email,
       password:password
    
      })
    
    
    
       })
       const response = await loginuser.json()
    
       if(response.success===true){
       localStorage.setItem("token",response.token)
       
      setTimeout(() => {
        navi("/home")
      }, 1000);
       
      { <Navbar/>}
      setvalid({
        color:"green"
      })
      setTimeout(() => {
        setvalid(null)
      }, 1000);
    
       }
       else{
        setvalid("username or password may be wrong")
      setTimeout(() => {
        setvalid(null)
      }, 2000);
       }
       

    }
      



   
    
    
  
    
  




  return (
    <>
      
       <div  >
    
        <header>
            
            <nav>
                <div class="logo-parent">
                    <a class="heading" href="./">
                        <img src="./assets/img/logo.png" alt="logo" width="80px"/>
                    </a>
                </div>                
            </nav>
        </header>
        
        <main id="main" >
            
            <section class="login-page" >
           
                <div class="login-content">
                
                    <h1>Login</h1>
                    <form action="./">
                        <input type="email" required name="email" id="email" onChange={onchange} placeholder="email" />
                        <input type="password" required name="password" id="password" onChange={onchange} minlength="8" maxlength="20"
                            placeholder="password" />
                        <button onClick={Login  }>Sign in</button>
                    </form> <Link to="/" style={{color:"white"}}>Don't have an account?</Link>
                </div>
            </section>

        </main>
        <div style={{color:"red" ,margin:"130px 500px"}}><strong>{valid}</strong></div>
               
    </div>
    </>
  )
}
