import React, { useState } from 'react';
import productcontext from './Productcontext';
import {use} from "react-router-dom"

const Productstate=(props)=> {
    const [allproducts,setallproducts]=useState([]);
    const [singleproduct,setsingleproduct]=useState([]);
    const [user,setuser]=useState([]);
    const getproducts= async()=>{
   const product = await fetch("http://localhost:5000/api/product",{

   method:"GET",
   headers:{
    "content-type":"application/json",
    "auth-token":localStorage.getItem("token")

   }
    
   })
   let response = await product.json();
   if(response.success===true){

   
   console.log(response.products)
   setallproducts(response.products);
    
   }
  


    }
   const productdetails = async (id)=>{
    const details = await fetch(`http://localhost:5000/api/product/${id}`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
     
    })
    const response = await details.json()
    console.log(response.products)
    if(response.success===true){
     setsingleproduct(response.products)
     console.log(response)
     console.log(singleproduct)

    }  


   }

   const getuser=async()=>{
    const userdetails = await fetch(`http://localhost:5000/api/account`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
    
     
    })
    const response= await userdetails.json();
    if(response.success===true){
     setuser(response.currentuser)
   console.log(setuser)

    }
    

   }




  return (
    <productcontext.Provider value={{getproducts ,allproducts,singleproduct,productdetails}}>
        {props.children}
    </productcontext.Provider>
  )
}
export default Productstate;
