import React, { useContext, useState } from 'react';
import productcontext from '../context/Productcontext';
import {useNavigate} from "react-router-dom";
import img from "../wardrobe/assets/img/logo.png"


 export default function Navbar() {
  const navigation = useNavigate();
  const[searchs,setsearchs]=useState()
  const {getuser,user,getcart,cart,searchitem,setsearch,search} = useContext(productcontext);
  const onclick= async()=>{
   await getuser();
    if(user.success===true){
     navigation("/profile")

    }
  }
    const oncart=async()=>{
   await getcart();
   if(cart.success===true){
    navigation("/cart")
   }
   else{
    
    navigation("/cart")
   }


    }
    const onchange=(e)=>{
  e.preventDefault();
  setsearch(
    e.target.value
  )

  

    }
    const onsearch=(e)=>{
      e.preventDefault()
    navigation({
      pathname:"/search",
     search:`?name=${search}`
    }) }
    const onsearchmen=(e)=>{
      e.preventDefault()
    navigation({
      pathname:"/search",
     search:`?category=${search}`
    })
    
    }
  return (
    <>
       <nav className="navbar sticky-top navbar-expand-lg bg-body-tertiary">
  <div className="container-fluid">
    <img src={img} style={{height:"50px",width:"80px"}} ></img>
    {/* <a className="navbar-brand" href="#">Shop</a> */}
    <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span className="navbar-toggler-icon"></span>
    </button>
    <div className="collapse navbar-collapse" id="navbarSupportedContent">
      <ul className="navbar-nav me-auto mb-2 mb-lg-0 ">
        <li className="nav-item">
          <a className="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        
        <li className="nav-item  " >
          <a className="btn  " aria-current="page" onClick={onsearchmen} >men</a>
        </li>
        <li className="nav-item  ">
          <a className="nav-link active text-center fw-semibold " aria-current="page" href="#">women</a>
        </li>
        <li className="nav-item  ">
          <a className="nav-link active text-center fw-semibold " aria-current="page" href="#">kids</a>
        </li>
        
        </ul>
      
        <form class="d-flex" role="search" onSubmit={onsearch}>
        <input class="form-control me-2" type="search" value={search} placeholder="Search" aria-label="Search" onChange={onchange} required/>
        <button class="btn btn-outline-success" type="submit" onSubmit={onsearch}>Search</button>
      </form>
      <i class="fa-solid fa-cart-shopping fa-xl mx-3" style={{cursor:"pointer"}} onClick={oncart}></i>
      <i class="fa-solid fa-user fa-xl mx-2" style={{cursor:"pointer"}} onClick={onclick}></i>
    </div>
  </div>
</nav>





    </>
  )}
