import React, { useState } from 'react';
import productcontext from './Productcontext';


const Productstate=(props)=> {
  
      const [allproducts,setallproducts]=useState([]);
    const [singleproduct,setsingleproduct]=useState([]);
    const [user,setuser]=useState([]);
    const [cart,setcart]=useState([]);
    const [spinner,setspinner]=useState();
    const [loading,setloading]=useState();
    const [addid,setaddid]=useState();
    const [valid,setvalid]= useState({
      color:"",
      message:""
    });
    let[total,settotal]=useState()
    

    
    const getproducts= async()=>{
      setspinner(true)
      // setloading(20)
   const product = await fetch("http://localhost:5000/api/product",{

   method:"GET",
   headers:{
    "content-type":"application/json",
    "auth-token":localStorage.getItem("token")

   }
    
   })
  //  setloading(60)
   let response = await product.json();
   if(response.success===true){

   
  //  setloading(80)
   setallproducts(response.products);
  //  setloading(100)
   setspinner(false)
    
   }
  


    }
   const productdetails = async (id)=>{
    setloading(30)
    setspinner(true)
    const details = await fetch(`http://localhost:5000/api/product/${id}`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
     
    })
    setloading(60)
    const response = await details.json()
    if(response.success===true){
      setloading(80)
     setsingleproduct(response.products)
     setloading(100)
     setspinner(false)
     

    }  


   }

   const getuser=async()=>{
    setloading(30)
    const userdetails = await fetch(`http://localhost:5000/api/account`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
    
     
    })
    setloading(60)
    const response= await userdetails.json();
    if(response.success===true){
      setloading(80)
     setuser(response)
     setloading(100)
   console.log(setuser);
   

    }
    

   }
   const getcart=async()=>{
     
    setloading(30)
    const getitems = await fetch(`http://localhost:5000/api/cart`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    },
    
     
    })
    setloading(60)
    
    const response= await getitems.json();
    
    if(response.success===true){
      setloading(80)
     setcart(response)
     settotal(response.totalprice)
     setloading(100)
     }
     

   }


   const addtocart=async(product_id,quantity,size)=>{
     

    const addtocart = await fetch(`http://localhost:5000/api/cart`,{

    method:"POST",
    headers:{
    "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    },
    body: JSON.stringify({product_id,quantity,size})
    
     
    })
    const response =await addtocart.json()
    if(response.success===true){
       
   console.log("we are in if block")
    }
    


   }
   const deletecartitem=async(id)=>{
  
    const details = await fetch(`http://localhost:5000/api/cart/${id}`,{

    method:"DELETE",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
     
    })
    const response=details.json()
    if(response.success===true){
    let remaings= cart.getItems.filter((product)=>{
      return product._id !=id
    })
    setcart(remaings)
    console.log("done")
    }

   }
   const updatecart=async(id,quantity)=>{
  
    const details = await fetch(`http://localhost:5000/api/cart/${id}`,{

    method:"PUT",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    },
    body:JSON.stringify({quantity})
     
    })
    const response= details.json();
    if(response.success===true){
      settotal(response.totalprice)
    }



   }



  return (
    <productcontext.Provider value={{getproducts ,allproducts,singleproduct,productdetails,getuser,user,getcart,cart,spinner,loading,setloading,addtocart,addid,setaddid,valid,setvalid,deletecartitem,updatecart}}>
        {props.children}
    </productcontext.Provider>
  )
}
export default Productstate;
