import React,{useEffect,useContext} from 'react';
import Productitem from './productitem';
import productcontext from '../context/Productcontext';
import {useNavigate} from "react-router-dom"

export default function Search() {

    const {allproducts,searchitem} =useContext(productcontext);
    const navigation=useNavigate();
    
    useEffect(()=>{
   if(localStorage.getItem("token")){
    searchitem() }
     else{
   navigation("/signup")
     }
},[])

  return (
    <>
     
     <div className=' container row my-3 mx-4'>
      <div className="container mx-3">{allproducts.length===0 && "no item in cart"}</div>
       { allproducts.map((allproduct)=>{
  return <Productitem key={Product._id} products={allproduct}   />
 })}
    </div>
      
    </>
  )
}
