import React, { useContext, useEffect } from 'react'
import productcontext from '../context/Productcontext.js'
import Orderitem from './orderitem.js'

export default function Showorders() {
    const {order,productdetails,singleproduct}= useContext(productcontext)
     
       var x=[...order]
        x.reverse()
      
     
   
  return (
    <>
    < div className='container my-3 mx-4 '>
     <div>{order.length===0?"no orders yet":""}</div>
       <div>
        {  x.map((o)=>{
          const product_info=async()=>{
        await productdetails(o.cartdata[0].product_id[0])


          }
          var x =product_info()



          return <Orderitem key= {o._id} order={o} product={singleproduct} />

        })  } 
       </div>
       </div>
    </>
  )
}
