import React, { useState } from 'react';
import productcontext from './Productcontext';


const Productstate=(props)=> {
  
      const [allproducts,setallproducts]=useState([]);
    const [singleproduct,setsingleproduct]=useState([]);
    const [user,setuser]=useState([]);
    const [cart,setcart]=useState([]);
    const [spinner,setspinner]=useState();
    const [loading,setloading]=useState();
    const getproducts= async()=>{
      setspinner(true)
      setloading(20)
   const product = await fetch("http://localhost:5000/api/product",{

   method:"GET",
   headers:{
    "content-type":"application/json",
    "auth-token":localStorage.getItem("token")

   }
    
   })
   setloading(60)
   let response = await product.json();
   if(response.success===true){

   
   setloading(80)
   setallproducts(response.products);
   setloading(100)
   setspinner(false)
    
   }
  


    }
   const productdetails = async (id)=>{
    setloading(30)
    setspinner(true)
    const details = await fetch(`http://localhost:5000/api/product/${id}`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
     
    })
    setloading(60)
    const response = await details.json()
    if(response.success===true){
      setloading(80)
     setsingleproduct(response.products)
     setloading(100)
     setspinner(false)
     

    }  


   }

   const getuser=async()=>{
    setloading(30)
    const userdetails = await fetch(`http://localhost:5000/api/account`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    }
    
     
    })
    setloading(60)
    const response= await userdetails.json();
    if(response.success===true){
      setloading(80)
     setuser(response)
     setloading(100)
   console.log(setuser);
   

    }
    

   }
   const getcart=async()=>{
     
    const getitems = await fetch(`http://localhost:5000/api/cart`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    },
    
     
    })
    
    const response= await getitems.json();
    
    if(response.success===true){
     setcart(response)
     }
     

   }


   const addtocart=async()=>{

    const addtocart = await fetch(`http://localhost:5000/api/cart`,{

    method:"GET",
    headers:{
     "content-type":"application/json",
     "auth-token":localStorage.getItem("token")
 
    },
    
     
    })


   }



  return (
    <productcontext.Provider value={{getproducts ,allproducts,singleproduct,productdetails,getuser,user,getcart,cart,spinner,loading,setloading}}>
        {props.children}
    </productcontext.Provider>
  )
}
export default Productstate;
