import React, { useState } from 'react';
import {Link,useNavigate} from "react-router-dom"
export default function Login() {

  const [input,setinput] = useState({email:"",password:""})
  const [navigate,setnavigate] = useState()
  const onchange =(e)=>{
    e.preventDefault();
    setinput({
    ...input,[e.target.name]:e.target.value
  
    }) }
    
    const login = async(e)=>{
        e.preventDefault();
        const {email,password}=input;
        
       const loginuser = await fetch("http://localhost:5000/api/auth",{
    
      headers:{"content-type":"application/json"},
      method:"POST",
      body:JSON.stringify({
       email:email,
       password:password
    
      })
    
    
    
       })
       const response = await loginuser.json()
       console.log(response)
       if(response.success===true){
       localStorage.setItem("token",response.token)
       setnavigate(response.success)
       
    
    
       }

    }
      
   const Nav=()=>{

    if(navigate===true){
     const navigation = useNavigate();
     navigation("/signup")


    }


   }
    
    
  
    
  




  return (
    <>
       <body class="login-body">
    
        <header>
            
            <nav>
                <div class="logo-parent">
                    <a class="heading" href="./">
                        <img src="./assets/img/logo.png" alt="logo" width="80px"/>
                    </a>
                </div>                
            </nav>
        </header>
        
        <main id="main">
            <section class="login-page">
                <div class="login-content">
                    <h1>Login</h1>
                    <form action="./">
                        <input type="email" required name="email" id="email" onChange={onchange} placeholder="email" />
                        <input type="password" required name="password" id="password" onChange={onchange} minlength="8" maxlength="20"
                            placeholder="password" />
                        <button onClick={login }>Sign in</button>
                    </form> <Link to="/signup">Don't have an account?</Link>
                </div>
            </section>
        </main>
               
    </body>
    </>
  )
}
