import React, { useContext, useState } from 'react'
import productcontext from '../context/Productcontext'

export default function Recoverpassword() {
  const {otp_req,otp_match,password_res} = useContext(productcontext)
  const [matchpass,setmatchpass]= useState("red")
  const [credentials,setcredentials] = useState({email:"",OTP1:null,OTP:null,password:""})
  const [otpmsg,setotpmsg] = useState()
  let [msg,setmsg]=useState()
 const onchange = (e)=>{
  e.preventDefault();
  setcredentials({
    ...credentials,[e.target.name]:e.target.value
  })
  console.log(credentials)
 }
  const matchpassword =(e)=>{
  e.preventDefault()
    if(document.getElementById("password").value===document.getElementById("pass2").value){
      setmatchpass("green")
      
    }
    else {
      setmatchpass("red")
    }
  }
  const on_request=async(e)=>{
    e.preventDefault()
    if(credentials.email.length==0){

    }
   else{ await otp_req(credentials.email)
     setmsg(`An OTP has been Sent to the entered email account `)}
  }
  const onmatching_otp = async()=>{
    credentials.OTP=localStorage.getItem("OTP")
    await otp_match(credentials)
    if(password_res===true){
      setotpmsg("password chnaged")
    }
  }
  return (
    <>
      <div className="card text-center" style={{width: "400px",margin:"150px 600px"}}>
    <div className="card-header h5 text-white bg-primary">Password Reset</div>
    <div className="card-body px-5">
        <p className="card-text py-2" >
           {msg}
        </p>
        <form onSubmit={on_request} style={{marginTop:"70px"}}>
        <div className="form-outline">
            <input type="email" id="Email" onChange={onchange} name="email" className="form-control my-3" placeholder='Email Input' required minLength={2}/>
            
        </div>
        <button type="submit" onClick={on_request} className="btn btn-primary w-100">send email OTP</button>
        </form>
        <div className="form-outline ">
            <input type="text" id="typeEmail" onChange={onchange} name="OTP1" className="form-control my-3 mt-4" placeholder='Enter OTP'/>
           
        </div>
        <button href="#" className="btn btn-primary w-100">verify code</button> 
        <div className="form-outline ">
            <input type="password" id="password" name='password' onChange={onchange} className="form-control my-4 mt-4" placeholder='Enter New Password' />
            
        </div>
        <div className="form-outline ">
            <input type="password" id="pass2" onChange={matchpassword} className="form-control my-3 mt-4" placeholder='Confirm Password' style={{borderBlockColor:matchpass,borderWidth:"3px"}} />
           
        </div>
        <button href="#" className="btn btn-primary w-100" onClick={onmatching_otp}>reset password</button>
        <div className="d-flex justify-content-between mt-4">
            <a className="" href="#">Login</a>
            <a className="" href="#">Register</a>
        </div>
    </div>
</div>
    </>
  )
}
