import React,{useContext, useState} from 'react';
import productcontext from '../context/Productcontext';

export default function Cartitem(props) {
  const{deletecartitem,getcart,updatecart}= useContext(productcontext);
      const {item}=props;
      let[quantity,setquantity]= useState(item.quantity)
      const onplus=async()=>{
       setquantity(quantity+1)
      
       await updatecart(item._id,quantity+1)
      }
      const onminus=async()=>{
        if(quantity>1){
        setquantity(quantity-1)
        await updatecart(item._id,quantity-1)
      }}
      const ondelete=async()=>{
       await deletecartitem(item._id)
       await getcart()
      }      
  return (
    <>


        <div class="row my-3">
              <div class="col-lg-3 col-md-12 mb-4 mb-lg-0">{console.log(item)}
                {/* <!-- Image --> */}
                <div class="bg-image hover-overlay hover-zoom ripple rounded" data-mdb-ripple-color="light">
                  <img src={item.product_id[0].image.url}
                    class="w-100" alt="Blue Jeans Jacket" />
                  <a href="#!">
                    <div class="mask" style={{backgroundColor: "rgba(251, 251, 251, 0.2)"}}></div>
                  </a>
                </div>
                {/* <!-- Image --> */}
              </div>

               <div class="col-lg-5 col-md-6 mb-4 mb-lg-0">
                 {/* <!-- Data --> * */}
                <p><strong>{item.name}</strong></p>
                <p>Color: blue</p>
                <p>Size:{item.size}</p>
                <button type="button" class="btn btn-primary btn-sm me-1 mb-2" data-mdb-toggle="tooltip"
                  title="Remove item">
                  <i class="fas fa-trash" onClick={ondelete}></i>
                </button>
                <button type="button" class="btn btn-danger btn-sm mb-2" data-mdb-toggle="tooltip"
                  title="Move to the wish list">
                  <i class="fas fa-heart"></i>
                </button>
                 {/* <!-- Data -->  */}
              </div> 

              <div class="col-lg-4 col-md-6 mb-4 mb-lg-0">
                {/* <!-- Quantity --> */}
                <div class="d-flex mb-4" style={{maxWidth: "300px"}}>
                  <button class="btn btn-primary px-3 me-2" onClick={onminus}
                    >
                    <i class="fas fa-minus" onClick={onminus}></i>
                  </button>

                  <div class="form-outline">
                    <input id="form1" min="0" name="quantity" value="1" type="number" class="form-control" value={quantity} />
                    <label class="form-label" for="form1">{item.stock}</label>
                  </div>

                  <button class="btn btn-primary px-3 ms-2"
                    onClick={onplus}>
                    <i class="fas fa-plus" onClick={onplus}></i>
                  </button>
                </div>
                {/* <!-- Quantity --> */}

                {/* <!-- Price --> */}
                <p class="text-start text-md-center">
                  <strong>total cost:${item.product_id[0].price * quantity}</strong>
                </p>
                {/* <!-- Price --> */}
              </div>
            </div>
    </>
  )
}
